mapper.insert:
		INSERT INTO RESERVATION_ORDER (
		   reservation_date
		  ,user_id
		  ,timeslot_id
		) VALUES (
		   date_add(NOW(), interval 9 hour)
		  ,#{userID} 
		  ,#{timeslotID} 
		)

        	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE TIMESLOT SET
   count = count + 1
WHERE timeslot_id = #{timeslotID}

UPDATE TIMESLOT SET
   count = count - 1
WHERE timeslot_id = #{timeslotID}
